import React, { useEffect, useState } from "react";

function Checklist() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    fetch("http://localhost:4000/api/uploads")
      .then(res => res.json())
      .then(data => setFiles(data.files || []));
  }, []);

  return (
    <div
      style={{
        maxWidth: 800,
        margin: "40px auto",
        padding: 32,
        background: "#fff",
        borderRadius: 16,
        boxShadow: "0 4px 24px rgba(30,41,59,0.10)",
        fontFamily: "Segoe UI, sans-serif"
      }}
    >
      <h1 style={{
        textAlign: "center",
        color: "#1a237e",
        marginBottom: 32,
        fontWeight: 700,
        letterSpacing: 1
      }}>
        ไฟล์ที่ลูกค้าอัปโหลด
      </h1>
      {files.length === 0 ? (
        <div style={{ textAlign: "center", color: "#888", padding: 32 }}>
          ไม่มีไฟล์ที่อัปโหลด
        </div>
      ) : (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
            gap: 32
          }}
        >
          {files.map(file => (
            <div
              key={file}
              style={{
                background: "#f8fafc",
                borderRadius: 12,
                boxShadow: "0 2px 8px rgba(30,41,59,0.06)",
                padding: 16,
                textAlign: "center",
                transition: "box-shadow 0.2s",
                border: "1px solid #e2e8f0"
              }}
            >
              <a
                href={`http://localhost:4000/uploads/${file}`}
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  color: "#2563eb",
                  fontWeight: 500,
                  textDecoration: "none",
                  wordBreak: "break-all"
                }}
                title="ดาวน์โหลด/เปิดไฟล์"
              >
                {file}
              </a>
              <div style={{ margin: "16px 0 0 0" }}>
                <img
                  src={`http://localhost:4000/uploads/${file}`}
                  alt={file}
                  style={{
                    width: "100%",
                    maxWidth: 180,
                    maxHeight: 180,
                    objectFit: "cover",
                    borderRadius: 8,
                    boxShadow: "0 1px 4px rgba(30,41,59,0.10)"
                  }}
                  onError={e => {
                    e.target.onerror = null;
                    e.target.style.display = "none";
                  }}
                />
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Checklist;
