import React, { useState } from "react";
import "./Register.css"; // ใช้ CSS เดิม หรือสร้าง home.css เพิ่มเติม

function Home() {
  // สถานะเปิด/ปิดระบบ
  const [systemOn, setSystemOn] = useState(true);

  // สถานะเลือกประเภท (รูปภาพ/ข้อความ)
  const [mode, setMode] = useState("image");

  // สถานะช่องกรอกข้อมูล
  const [second, setSecond] = useState("");
  const [minute, setMinute] = useState("");
  const [hour, setHour] = useState("");
  const [price, setPrice] = useState("");

  // ฟังก์ชันส่งข้อมูลไปยัง backend หรือเว็บ user (mock)
  const handleSave = () => {
    // ส่งข้อมูลไป backend หรือ websocket เพื่อ sync กับฝั่ง user
    alert(
      `บันทึกสำเร็จ\nประเภท: ${mode === "image" ? "รูปภาพ" : "ข้อความ"}\nเวลา: ${hour} ชม. ${minute} นาที ${second} วินาที\nราคา: ${price} บาท`
    );
  };

  // ฟังก์ชันเปิด/ปิดระบบ
  const handleToggleSystem = () => {
    setSystemOn((prev) => !prev);
    // ส่งสถานะไป backend เพื่อ sync กับ user
  };

  return (
    <div className="register-container">
      <h1>Admin Home</h1>
      <div className="register-form">
        <div style={{ display: "flex", alignItems: "center", marginBottom: 20 }}>
          <span style={{ marginRight: 10 }}>สถานะระบบ:</span>
          <button
            style={{
              background: systemOn ? "#4caf50" : "#f44336",
              color: "#fff",
              border: "none",
              borderRadius: 20,
              width: 60,
              height: 30,
              cursor: "pointer",
              marginRight: 10,
            }}
            onClick={handleToggleSystem}
          >
            {systemOn ? "เปิด" : "ปิด"}
          </button>
          <div
            style={{
              width: 40,
              height: 20,
              background: "#ccc",
              borderRadius: 10,
              position: "relative",
              marginLeft: 10,
              display: "inline-block",
            }}
          >
            <div
              style={{
                width: 18,
                height: 18,
                background: systemOn ? "#4caf50" : "#f44336",
                borderRadius: "50%",
                position: "absolute",
                left: systemOn ? 20 : 2,
                top: 1,
                transition: "left 0.2s",
              }}
            ></div>
          </div>
        </div>

        <div style={{ marginBottom: 20 }}>
          <span>ตั้งค่าสำหรับ:</span>
          <button
            className={mode === "image" ? "active" : ""}
            style={{
              margin: "0 10px",
              background: mode === "image" ? "#1976d2" : "#eee",
              color: mode === "image" ? "#fff" : "#333",
              border: "none",
              borderRadius: 5,
              padding: "5px 15px",
              cursor: "pointer",
            }}
            onClick={() => setMode("image")}
            disabled={!systemOn}
          >
            รูปภาพ
          </button>
          <button
            className={mode === "text" ? "active" : ""}
            style={{
              background: mode === "text" ? "#1976d2" : "#eee",
              color: mode === "text" ? "#fff" : "#333",
              border: "none",
              borderRadius: 5,
              padding: "5px 15px",
              cursor: "pointer",
            }}
            onClick={() => setMode("text")}
            disabled={!systemOn}
          >
            ข้อความ
          </button>
        </div>

        <div style={{ marginBottom: 20 }}>
          <label>ตั้งเวลา (เลือกอย่างน้อย 1 ช่อง):</label>
          <div style={{ display: "flex", gap: 10 }}>
            <input
              type="number"
              min="1"
              max="59"
              placeholder="วินาที"
              value={second}
              onChange={(e) => setSecond(e.target.value)}
              disabled={!systemOn}
              style={{ width: 80 }}
            />
            <input
              type="number"
              min="1"
              max="59"
              placeholder="นาที"
              value={minute}
              onChange={(e) => setMinute(e.target.value)}
              disabled={!systemOn}
              style={{ width: 80 }}
            />
            <input
              type="number"
              min="1"
              max="24"
              placeholder="ชั่วโมง"
              value={hour}
              onChange={(e) => setHour(e.target.value)}
              disabled={!systemOn}
              style={{ width: 80 }}
            />
          </div>
        </div>

        <div style={{ marginBottom: 20 }}>
          <label>ตั้งราคา (บาท):</label>
          <input
            type="number"
            min="1"
            placeholder="ราคา"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            disabled={!systemOn}
            style={{ width: 120 }}
          />
        </div>

        <button
          onClick={handleSave}
          disabled={!systemOn}
          style={{
            background: "#1976d2",
            color: "#fff",
            border: "none",
            borderRadius: 5,
            padding: "10px 30px",
            cursor: systemOn ? "pointer" : "not-allowed",
            fontSize: "1rem",
          }}
        >
          บันทึก
        </button>
        {!systemOn && (
          <div style={{ color: "red", marginTop: 20 }}>
            * ระบบถูกปิด ฝั่ง user จะไม่สามารถเลือกส่งภาพหรือข้อความได้
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
