import React, { useEffect, useState } from "react";

function Checklist() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    fetch("http://localhost:4000/api/uploads")
      .then(res => res.json())
      .then(data => setFiles(data.files || []));
  }, []);

  return (
    <div>
      <h1>ไฟล์ที่ลูกค้าอัปโหลด</h1>
      <ul>
        {files.map(file => (
          <li key={file}>
            <a href={`http://localhost:4000/uploads/${file}`} target="_blank" rel="noopener noreferrer">
              {file}
            </a>
            <br />
            <img src={`http://localhost:4000/uploads/${file}`} alt={file} width={200} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Checklist;
