import React, { useEffect, useState } from 'react';

function AdminReport() {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5001/api/admin/reports')
      .then(res => res.json())
      .then(data => {
        setReports(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  return (
    <div>
      <h2>รายการแจ้งปัญหาจากผู้ใช้</h2>
      {loading ? (
        <div>กำลังโหลด...</div>
      ) : reports.length === 0 ? (
        <div>ยังไม่มีรายงาน</div>
      ) : (
        <ul>
          {reports.map((r, idx) => (
            <li key={idx}>
              <strong>หมวดหมู่:</strong> {r.category} <br />
              <strong>รายละเอียด:</strong> {r.detail} <br />
              <strong>เวลา:</strong> {new Date(r.date).toLocaleString()}
              <hr />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default AdminReport;
