import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./ImageQueue.css";

function ImageQueue() {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchImages();
    // รีเฟรชทุก 5 วินาที
    const interval = setInterval(fetchImages, 5000);
    return () => clearInterval(interval);
  }, []);

  const fetchImages = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/queue");
      if (response.ok) {
        const data = await response.json();
        setImages(data);
      }
    } catch (error) {
      console.error("Error fetching images:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleImageClick = (image) => {
    setSelectedImage(image);
    setShowModal(true);
  };

  const handleApprove = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/approve/${id}`, {
        method: "POST",
      });
      if (response.ok) {
        alert("อนุมัติเรียบร้อย");
        fetchImages(); // รีเฟรชรายการ
        setShowModal(false);
      }
    } catch (error) {
      console.error("Error approving image:", error);
      alert("เกิดข้อผิดพลาด");
    }
  };

  const handleReject = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/reject/${id}`, {
        method: "POST",
      });
      if (response.ok) {
        alert("ปฏิเสธเรียบร้อย");
        fetchImages(); // รีเฟรชรายการ
        setShowModal(false);
      }
    } catch (error) {
      console.error("Error rejecting image:", error);
      alert("เกิดข้อผิดพลาด");
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('th-TH');
  };

  if (loading) {
    return (
      <div className="image-queue-container">
        <header className="queue-header">
          <Link to="/" className="back-btn">← กลับ</Link>
          <h1>คิวรูปภาพ</h1>
        </header>
        <div className="loading">กำลังโหลด...</div>
      </div>
    );
  }

  return (
    <div className="image-queue-container">
      <header className="queue-header">
        <Link to="/" className="back-btn">← กลับ</Link>
        <h1>คิวรูปภาพ ({images.length})</h1>
        <button onClick={fetchImages} className="refresh-btn">รีเฟรช</button>
      </header>

      <main className="queue-content">
        {images.length === 0 ? (
          <div className="no-images">ไม่มีรูปภาพในคิว</div>
        ) : (
          <div className="images-grid">
            {images.map((image) => (
              <div key={image.id} className="image-card" onClick={() => handleImageClick(image)}>
                <div className="image-preview">
                  <img 
                    src={`http://localhost:5000${image.filePath}`} 
                    alt="Preview"
                    onError={(e) => {
                      e.target.src = '/placeholder-image.png';
                    }}
                  />
                  {image.text && (
                    <div 
                      className="image-overlay-text"
                      style={{ 
                        color: image.textColor || 'white',
                        textShadow: image.textColor === 'white' ? '0 0 4px #000' : 'none'
                      }}
                    >
                      {image.text}
                    </div>
                  )}
                </div>
                <div className="image-info">
                  <p><strong>ผู้ส่ง:</strong> {image.sender}</p>
                  <p><strong>เวลา:</strong> {image.time} นาที</p>
                  <p><strong>ราคา:</strong> {image.price} บาท</p>
                  <p><strong>ส่งเมื่อ:</strong> {formatDate(image.createdAt)}</p>
                  <p><strong>สถานะ:</strong> 
                    <span className={`status-badge status-${image.status}`}>
                      {image.status === 'pending' ? 'รอดำเนินการ' : 
                       image.status === 'approved' ? 'อนุมัติ' : 'ปฏิเสธ'}
                    </span>
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>

      {showModal && selectedImage && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-btn" onClick={() => setShowModal(false)}>&times;</span>
            <h2>รายละเอียดรูปภาพ</h2>
            
            <div className="modal-image-container">
              <img 
                src={`http://localhost:5000${selectedImage.filePath}`} 
                alt="Full preview"
                className="modal-image"
              />
              {selectedImage.text && (
                <div 
                  className="modal-image-text"
                  style={{ 
                    color: selectedImage.textColor || 'white',
                    textShadow: selectedImage.textColor === 'white' ? '0 0 4px #000' : 'none'
                  }}
                >
                  {selectedImage.text}
                </div>
              )}
            </div>

            <div className="modal-details">
              <p><strong>ผู้ส่ง:</strong> {selectedImage.sender}</p>
              <p><strong>เวลาที่เลือก:</strong> {selectedImage.time} นาที</p>
              <p><strong>ราคา:</strong> {selectedImage.price} บาท</p>
              <p><strong>ส่งเมื่อ:</strong> {formatDate(selectedImage.createdAt)}</p>
              <p><strong>สถานะ:</strong> 
                <span className={`status-badge status-${selectedImage.status}`}>
                  {selectedImage.status === 'pending' ? 'รอดำเนินการ' : 
                   selectedImage.status === 'approved' ? 'อนุมัติ' : 'ปฏิเสธ'}
                </span>
              </p>
            </div>

            {selectedImage.status === 'pending' && (
              <div className="modal-actions">
                <button 
                  className="approve-btn"
                  onClick={() => handleApprove(selectedImage.id)}
                >
                  อนุมัติ
                </button>
                <button 
                  className="reject-btn"
                  onClick={() => handleReject(selectedImage.id)}
                >
                  ปฏิเสธ
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default ImageQueue;
